import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { User } from '../user';
import { UserService } from '../user.service';
import { Location } from '@angular/common';
import { AuthenticationService } from '../authentication.service';
import { throwError } from 'rxjs';


@Component({
  selector: 'app-edit-profile',
  templateUrl: './edit-profile.component.html',
  styleUrls: ['./edit-profile.component.css']
})
export class EditProfileComponent {

  image1 = "data:image/jpeg;base64,";
  image2 = "data:image/jpeg;base64,";
  image3 = "data:image/jpeg;base64,";
  image4 = "data:image/jpeg;base64,";
  image5 = "data:image/jpeg;base64,";
  image6 = "data:image/jpeg;base64,";
  
  
  user: User; 
  error: any;

  constructor(
    private location: Location,
    private userService: UserService,
    private route: ActivatedRoute,
    private authService: AuthenticationService
  ){}

  ngOnInit(): void {
    this.getUser();
  }

  getUser(): void {
    const id = String(this.route.snapshot.paramMap.get('id'));
    this.userService.getUser(id)
      .subscribe(user => {
        this.user = user
        if(this.authService.getUser()._id != this.user._id)
          throwError(() => new Error('test'));
      });
  }

  select1(): void{
    this.user.image = this.image1;
  }

  select2(): void{
    this.user.image = this.image2;
  }

  select3(): void{
    this.user.image = this.image3;
  }

  select4(): void{
    this.user.image = this.image4;
  }

  select5(): void{
    this.user.image = this.image5;
  }

  select6(): void{
    this.user.image = this.image6;
  }

  onSubmit(form:any): void{
    this.error = null;
    const username = form.value.fusername;
    if (!/^[a-zA-Z0-9]+$/.test(username)) {
      this.error = "Username must contain only numbers and letters.";
      return;
    } else if(username.length < 3 || username.length > 10){
      this.error = "Username must be between 3 and 10 characters long.";
      return;
    }
    this.userService.getUserByName(username)
    .subscribe(user => {
      if(user == null){
        this.submit(form, username);
      }else{
        this.error = "Username taken";
      }
    });
  }

  submit(form: any, username: any): void{
    this.user.username = username;
    form.resetForm(); 
    alert("Profile updated successfully!");
    this.save();
  }

  save(): void {
    if (this.user) {
      this.userService.updateUser(this.user)
        .subscribe(() => this.goBack());
    }
  }

  goBack(): void {
    this.location.back();
  }
}
